import React from 'react';
import Popup from '../Popup/Popup';

function PopupWithForm(props) {
  return (
    <Popup
      isOpen={props.isOpen}
      onClose={props.onClose}
    >
      <h2 className='popup__title'>{props.title}</h2>
      <form className='popup__form' action="">
        {props.children}
        <div className="popup__error-container ">
          <span className="popup__field-error avaLink-field-error popup__error-container-submit">errors.avatarLink</span>
        </div>
        <button className='popup__submit-btn'>{props.submitText}</button>
        <div className='popup__redirect'>
          <p className='popup__redirect-str'>or</p>
        </div>
      </form>
    </Popup>
  )
}

export default PopupWithForm;
