import propsCardImg from '../../images/card-card-image_06.png';
import propsInactiveTrash from '../../images/icon-trash-inactive.svg';

function NewsCard(params) {

  return (
    <li className='card'>
      <div className='card__image-container'>
        <img className='card__image' src={propsCardImg} alt="props-title" />
      </div>
      <p className='card__keyword element-absolute_style'>propsKeyword</p>
      <button className='card__btn element-absolute_style' style={{ backgroundImage: `url(${propsInactiveTrash})` }}></button>
      <p className='card__tooltip element-absolute_style'>propsTooltip</p>
      <div className='card__info-box'>
        <p className='card__date'>November 4, 2020</p>
        <h2 className='card__title'>Everyone Needs a Special 'Sit Spot' in Nature</h2>
        <p className='card__text'>Ever since I read Richard Louv's influential book, "Last Child in the Woods," the idea of having a special "sit spot" has stuck with me. This advice, which Louv attributes to nature educator Jon Young, is for both adults and children to find vvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvv</p>
        <p className='card__source'>treehugger</p>
      </div>
    </li >
  )
}

export default NewsCard;
